#!/bin/env perl
use YAML;# 'LoadFile';

use constant COUNT => 1043;

my $index = int rand COUNT;
my $i=0;
while ($i != $index)
{
    $i++;
    <DATA>;
}

my $data = Load(<DATA>);
my ($name,$txt) = each %$data;
print <<EOF;
-- $name --
$txt
EOF

